var naturalCompare = require('../../utils/natural-compare');

function naturalSorter(unlock1, unlock2) {
  return naturalCompare(unlock1[1], unlock2[1]);
}

function standardSorter(unlock1, unlock2) {
  return unlock1[1] > unlock2[1] ? 1 : -1;
}

function sortSelectors(selectors, method) {
  switch (method) {
  case 'natural':
    return selectors.sort(naturalSorter);
  case 'standard':
    return selectors.sort(standardSorter);
  case 'none':
  case false:
    return selectors;
  }
}

module.exports = sortSelectors;
